define([], function() {
  'use strict'

  return class FilterHandler {
    constructor(pattern) {
      this.pattern = pattern
    }

    base() {
      return {
        meta: {
          removable: false,
          index: this.pattern,
          negate: false,
          disabled: false,
          alias: null,
          type: 'phrase',
          key: null,
          value: null,
          params: {
            query: null,
            type: 'phrase'
          }
        },
        query: {
          match: null
        },
        $state: {
          store: 'appState'
        }
      }
    }

    agentQuery(agent) {
      const result = this.base()
      result.meta.key = 'agent.id'
      result.meta.value = agent
      result.meta.params.query = agent
      result.query.match = {
        'agent.id': {
          query: agent,
          type: 'phrase'
        }
      }
      return result
    }

    nodeQuery(node) {
      const result = this.base()
      result.meta.key = 'cluster.node'
      result.meta.value = node
      result.meta.params.query = node
      result.query.match = {
        'cluster.node': {
          query: node,
          type: 'phrase'
        }
      }
      return result
    }

    ruleGroupQuery(group) {
      const result = this.base()
      result.meta.key = 'rule.groups'
      result.meta.value = group
      result.meta.params.query = group
      result.query.match = {
        'rule.groups': {
          query: group,
          type: 'phrase'
        }
      }
      return result
    }

    ruleIdQuery(ruleId) {
      const result = this.base()
      result.meta.removable = true
      result.meta.key = 'rule.id'
      result.meta.value = ruleId
      result.meta.params.query = ruleId
      result.query.match = {
        'rule.id': {
          query: ruleId,
          type: 'phrase'
        }
      }
      return result
    }

    managerQuery(manager, isCluster) {
      const result = this.base()
      result.meta.key = isCluster ? 'cluster.name' : 'manager.name'
      result.meta.value = manager
      result.meta.params.query = manager
      result.query.match = isCluster
        ? {
            'cluster.name': {
              query: manager,
              type: 'phrase'
            }
          }
        : {
            'manager.name': {
              query: manager,
              type: 'phrase'
            }
          }
      return result
    }

    pciQuery() {
      const result = this.base()
      result.meta.type = 'exists'
      result.meta.value = 'exists'
      result.meta.key = 'rule.pci_dss'
      result.exists = {
        field: 'rule.pci_dss'
      }
      delete result.query
      return result
    }

    gdprQuery() {
      const result = this.base()
      result.meta.type = 'exists'
      result.meta.value = 'exists'
      result.meta.key = 'rule.gdpr'
      result.exists = {
        field: 'rule.gdpr'
      }
      delete result.query
      return result
    }
  }
})

/**
 * Returns applied configuration for specific agent and specific section
 * @param {string} agentId Agent ID
 * @param {Array<object>} sections Array that includes sections to be fetched
 * @param {object} apiReq API request service reference
 */
define([], function() {
  'use strict'
  return async function queryConfig(agentId, sections, apiReq) {
    try {
      if (
        !agentId ||
        typeof agentId !== 'string' ||
        !sections ||
        !sections.length ||
        typeof sections !== 'object' ||
        !Array.isArray(sections)
      ) {
        throw new Error('Invalid parameters')
      }

      const result = {}
      for (const section of sections) {
        const { component, configuration } = section
        if (
          !component ||
          typeof component !== 'string' ||
          !configuration ||
          typeof configuration !== 'string'
        ) {
          throw new Error('Invalid section')
        }
        const partialResult = await apiReq.apiReq(
          `/agents/${agentId}/config/${component}/${configuration}`
        )
        result[`${component}-${configuration}`] = partialResult.data.data
        if (partialResult.data.error) {
          result[`${component}-${configuration}`] = partialResult.data.message
        }
      }
      return result
    } catch (error) {
      return Promise.reject(error)
    }
  }
})

define([], function() {
  'use strict'
  return function objectWithoutProperties(obj) {
    try {
      const result = JSON.parse(
        JSON.stringify(obj, function(key, val) {
          if (key == '$$hashKey') {
            return undefined
          }
          return val
        })
      )
      return result
    } catch (error) {
      return {}
    }
  }
})

