define(['../module', 'splunkjs/mvc'], function(module) {
  'use strict'
  /**
   * Class that handles dynamic table methods
   */
  module.service('$dataService', function($requestService) {
    return class DataFactory {
      /**
       * Class constructor
       * @param {String} path
       * @param {Object} implicitFilter
       */
      constructor(path, implicitFilter) {
        this.implicitFilter = implicitFilter || false
        this.items = []
        this.path = path
        this.filters = []
        this.sortValue = false
        this.sortDir = false
        this.sortValue = false
        this.busy = false
        if (this.implicitFilter) this.filters.push(...this.implicitFilter)
      }

      /**
       * Sorts table by a value
       * @param {String} value
       */
      addSorting(value) {
        this.sortValue = value
        this.sortDir = !this.sortDir
      }

      /**
       * Removes filters added to table
       */
      removeFilters() {
        this.filters = []
        if (this.implicitFilter) this.filters.push(...this.implicitFilter)
      }

      /**
       * Serializes filters
       * @param {Object} parameters
       */
      serializeFilters(parameters) {
        if (this.sortValue) {
          parameters.sort = this.sortDir ? '-' + this.sortValue : this.sortValue
        }

        for (const filter of this.filters) {
          if (filter.value !== '') parameters[filter.name] = filter.value
        }
      }

      /**
       * Adds a filter to the table
       * @param {String} filterName
       * @param {String} value
       */
      addFilter(filterName, value) {
        this.filters = this.filters.filter(filter => filter.name !== filterName)
        if (typeof value !== 'undefined') {
          this.filters.push({
            name: filterName,
            value: value
          })
        }
      }

      /**
       * Performs a HTTP request for fetching data
       * @param {Object} options
       */
      async fetch(options = {}) {
        try {
          if (this.busy) return { items: this.items, time: 0 }
          this.busy = true
          const start = new Date()

          // If offset is not given, it means we need to start again
          if (!options.offset) this.items = []
          const offset = options.offset || 0
          const limit = options.limit || 500
          const parameters = { limit, offset }

          this.serializeFilters(parameters)

          // Fetch next <limit> items
          const firstPage = await $requestService.apiReq(this.path, parameters)
          this.items = this.items.filter(item => !!item)
          this.items.push(...firstPage.data.data.items)

          const totalItems = firstPage.data.data.totalItems

          const remaining =
            this.items.length === totalItems
              ? 0
              : totalItems - this.items.length

          // Ignore manager as an agent, once the team solves this issue, review this line
          if (this.path === '/agents')
            this.items = this.items.filter(item => item.id !== '000')

          if (remaining > 0) this.items.push(...Array(remaining).fill(null))

          const end = new Date()
          const elapsed = (end - start) / 1000
          this.busy = false
          return { items: this.items, time: elapsed }
        } catch (error) {
          this.busy = false
          return Promise.reject(error)
        }
      }

      /**
       * Resets table parameters
       */
      reset() {
        this.items = []
        this.filters = []
        this.sortValue = false
        this.sortDir = false
        this.sortValue = false
        if (this.implicitFilter) this.filters.push(...this.implicitFilter)
      }
    }
  })
})

define(['../module'], function(module) {
  'use strict'

  class DateDiffService {
    constructor() {
      this.start = null
      this.end = null
    }

    /**
     * Returns the difference between dates
     */
    getDateDiff(start, end) {
      this.start = new Date(start)
      this.end = new Date(end)
      const result = {
        duration: 'Unknown',
        inProgress: false,
        end: this.end || 'Unknown',
        start: this.start || 'Unknown'
      }
      if (this.end && this.start) {
        result.duration = (this.end - this.start) / 1000 / 60
        result.duration = Math.round(result.duration * 100) / 100
        if (result.duration <= 0) {
          result.inProgress = true
        }
      }
      return result
    }
  }

  module.service('$dateDiffService', DateDiffService)
})

define(['../module'], function(module) {
  'use strict'

  class FilterService {
    constructor($notificationService) {
      this.$notificationService = $notificationService
    }

    /**
     * Returns the filters
     * @returns {Array} the Array of filters
     */
    getFilters() {
      try {
        if (window.localStorage.filters)
          return JSON.parse(window.localStorage.filters)
        else return []
      } catch (err) {
        return []
      }
    }

    /**
     * Adds a new filter
     * @param {String} filter
     */
    addFilter(filter) {
      try {
        const filterJson = JSON.parse(filter)
        if (window.localStorage.filters) {
          const filters = JSON.parse(window.localStorage.filters)
          let isInIt = false
          filters.map(fil => {
            let key = Object.keys(filterJson)
            if (key.length > 1) {
              key = key[0]
            }
            if (fil[key]) {
              isInIt = true
              fil[key] = filterJson[key]
            }
          })
          if (!isInIt) {
            filters.push(filterJson)
          }
          window.localStorage.setItem('filters', JSON.stringify(filters))
        } else {
          window.localStorage.setItem('filters', `[${filter}]`)
        }
      } catch (err) {
        this.$notificationService.showSimpleToast(
          'Incorrent format. Please use key:value syntax'
        )
      }
    }

    /**
     * Returns the filters in a way that visualizations can handle
     * @returns {String} The serialized filters
     */
    getSerializedFilters() {
      try {
        let filterStr = ' '
        if (window.localStorage.filters)
          for (const filter of JSON.parse(window.localStorage.filters)) {
            if (typeof filter === 'object') {
              const key = Object.keys(filter)[0]
              filterStr += key
              filterStr += '='
              filterStr += filter[key]
              filterStr += ' '
            } else {
              filterStr += filter + ' '
            }
          }
        return filterStr
      } catch (err) {
        this.$notificationService.showSimpleToast('Error when getting filters.')
      }
    }

    /**
     * Removes a filter
     * @param {Object}: The filter to be removed
     */
    removeFilter(filter) {
      try {
        filter = JSON.parse(
          `{"${filter.split(':')[0]}":"${filter.split(':')[1]}"}`
        )
        const filters = JSON.parse(window.localStorage.filters)
        if (filters.length === 1) {
          delete window.localStorage.filters
          return
        }
        filters.map((item, index) => {
          if (Object.keys(item)[0] === Object.keys(filter)[0]) {
            filters.splice(index, 1)
          }
        })
        window.localStorage.setItem('filters', JSON.stringify(filters))
      } catch (err) {
        this.$notificationService.showSimpleToast('Error removing filter.')
      }
    }

    /**
     * Sets the filters empty
     */
    cleanFilters() {
      delete window.localStorage.filters
    }
  }
  module.service('$filterService', FilterService)
})

